import "./LogIn.css";
import "../App.css";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Box, Button, TextField } from "@mui/material";
import CFS_logo from "../assets/CFS_logo.png";

const Login = () => {
  const [id, setID] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate(); // 화면 이동 객체

  const handleLogin = async (e) => {
    e.preventDefault(); // form 컴포넌트에서 버튼을 클릭하면 생기는 새로고침을 막아줌.

    try {
      // 백엔드 서버의 로그인 로직과 통신하는 과정.
      const response = await fetch("로그인 서버 주소", {
        method: "POST",
        mode: "cors",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          id: id,
          password: password,
        }),
      });
      if (response.ok) {
        const result = await response.json();
        console.log(result);

        navigate("/Reports");
      } else {
        const errorData = await response.json();
        alert("아이디와 비밀번호를 다시 확인해주세요.");
      }
    } catch (error) {
      console.error("로그인 요청 중 오류 발생 : ", error);
      alert("로그인 요청 중 오류가 발생했습니다.");
    }
  };

  return (
    <div className="hide_banner">
      <div className="Login">
        <div className="text_center">
          <img
            src={CFS_logo}
            alt="로고"
            className="logo"
            />
        </div>
        <div className="top_empty_space">
          <form
            className="login_form"
            onSubmit={handleLogin}
          >
            <Box className="input_box">
              <TextField
                type="text"
                placeholder="ID"
                value={id}
                onChange={(e) => setID(e.target.value)}
                className="text_box"
                InputProps={{ sx: { borderRadius: "25px" }}}
              />
            </Box>

            <Box className="input_box">
              <TextField
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="text_box"
                InputProps={{ sx: { borderRadius: "25px"} }}
              />
            </Box>

            <Button
              marginTop= "10px"
              type="submit"
              variant="contained"
              className="submit_button"
            >
              로그인
            </Button>

            <p className="service_text">서비스 이용을 위해 로그인 해주세요.</p>
            <p className="find_id_password">
              아이디/비밀번호를 잊으셨나요?{" "}
              <Link to="/SignUpForm" style={{ color: "#C224DC" }}>
                아이디/비밀번호 찾기
              </Link>
            </p>

            <div className="line"></div>
            <p className="sign_up">계정이 없으신가요?{" "}
              <Link to="/SignUp" style={{ color: "#C224DC" }}>
                회원가입
              </Link>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
